import React, { useState } from "react";
import AliceCarousel from "react-alice-carousel";
import "react-alice-carousel/lib/alice-carousel.css";
import HomeSectionCard from "../HomeSectionCard/HomeSectionCard";
import { Button } from "@mui/material";
import KeyboardArrowRightIcon from "@mui/icons-material/KeyboardArrowRight";
import KeyboardArrowLeftIcon from "@mui/icons-material/KeyboardArrowLeft";
import { mens_kurta } from "../../Data/Men/men_kurta";

const SectionCarosel = () => {
    const [activeIndex, setActiveIndex] = useState(0);
  const responsive = {
    0: { items: 2 },
    720: { items: 4 },
    1024: { items: 5.5 },
    1570: {items : 7}
  };

  const slidePrev = () => setActiveIndex(activeIndex -1 );
  const slideNext = () => setActiveIndex(activeIndex +1 );

  const syncActiveIndex = ({item}) => setActiveIndex(item);

  const items = mens_kurta.slice(0,10).map((item) => <HomeSectionCard product={item}/>);
  return (
    <>
    <div className="border border-black">
    <div className="relative mx-[1%] p-[1.5%] mr-5">
      {activeIndex !==0 && <Button
          variant="contained"
          className="z-50 bg"
          sx={{ position: "absolute", top: "10rem", left: "0rem", transform:"rotate(90deg)"}}
          aria-label="previos"
          onClick={slidePrev}
        >
          <KeyboardArrowLeftIcon sx={{transform:"rotate(-90deg)"}} />
        </Button>
      }
        <AliceCarousel
          items={items}
          responsive={responsive}
          disableDotsControls
          disableButtonsControls
          onSlideChanged={syncActiveIndex}
          activeIndex={activeIndex}
        />
        {
            activeIndex !== items.length-5 && <Button
          variant="contained"
          className="z-50 absolute top-0 right-0 -translate-x-4"
          sx={{ position: "absolute", top: "8rem", right: "0rem", transform:"rotate(90deg)"}}
          aria-label="next"
          onClick={slideNext}
        >
          <KeyboardArrowRightIcon sx={{transform:"rotate(-90deg)"}} />
        </Button>
        }
      </div>
    </div>
    
    </>
  );
};

export default SectionCarosel;
