import React from "react";
import AliceCarousel from "react-alice-carousel";
import "react-alice-carousel/lib/alice-carousel.css";
import HomeSectionCard from "../HomeSectionCard/HomeSectionCard";
import { Button } from "@mui/material";
import KeyboardArrowRightIcon from "@mui/icons-material/KeyboardArrowRight";
import KeyboardArrowLeftIcon from "@mui/icons-material/KeyboardArrowLeft";

const SectionCarosel = () => {
  const responsive = {
    0: { items: 2 },
    720: { items: 4 },
    1024: { items: 6 },
  };

  const items = [1, 1, 1, 1, 1].map((item) => <HomeSectionCard />);
  return (
    <>
      <div className="relative mx-[1%] p-[1.5%] border border-black">
      <Button
          variant="contained"
          className="z-50"
          sx={{ position: "absolute", top: "10rem", left: "0rem", transform:"rotate(90deg)"}}
          aria-label="next"
        >
          <KeyboardArrowLeftIcon sx={{transform:"rotate(-90deg)"}} />
        </Button>
        <AliceCarousel
          autoPlay
          controlsStrategy="alternate"
          autoPlayInterval={2000}
          animationDuration={500}
          animationType="fadeout"
          infinite
          touchTracking={false}
          disableDotsControls
          disableButtonsControls
          items={items}
          responsive={responsive}
        />
        <Button
          variant="contained"
          className="z-50"
          sx={{ position: "absolute", top: "8rem", right: "0rem", transform:"rotate(90deg)"}}
          aria-label="next"
        >
          <KeyboardArrowRightIcon sx={{transform:"rotate(-90deg)"}} />
        </Button>
      </div>
    </>
  );
};

export default SectionCarosel;
