import React from "react";
import './ProductCard.css';

const ProductCard = ({product}) => {
  return (
    <>
      <div className="productCard w-[15rem] m-3 transition-all cursor-pointer">
        <div className="h-[20rem]">
          <img
            className="h-full w-full object-cover object-center"
            src={pro}
            alt=""
          />
        </div>

        <div className="textPart bg-white p-3">
          <div>
            <p className="font-bold opacity-60">
              Madstitches Apparels Private Limited
            </p>
            <p>Party Regular Sleeves Floral Print, Printed Women White...</p>
            <p className="text-gray opacity-60">white</p>
          </div>
          <div className="flex items-center space-x-2">
            <p className="font-semibold">Rs.199</p>
            <p className="line-through opacity-50">Rs.1999</p>
            <p className="text-green-600 semibold">70% OFF</p>
          </div>
        </div>
      </div>
    </>
  );
};

export default ProductCard;
